-- Records of blog_article
-- ----------------------------
INSERT INTO `blog_article` VALUES ('1', '《龙印OL》游戏介绍', '0', '1577170556', '2019-12-24 15:14:25', '', '《龙印》是一款中国武侠风格的手机游戏，玩家出生在元末背景下的中原，正是元廷腐败、硝烟四起的战乱时期。乱世中各方起义军与元廷激烈对抗，同时“龙印”的传说也牵动着各方势力的心。游戏中的四大阵营都在积极且秘密的寻找着龙印。而玩家作为其中一个阵营的成员，以对抗元军，推翻元廷统治为目标，在江湖上一边历练，一边寻找着传说中的龙印。随着剧情的展开玩家将先后与“韩山童”、“刘福通”、“朱元璋”等历史知名人物邂逅，展开一场别样的冒险旅程。', '已发布', '推荐', 'zhouquan', '1', '1');
