-- ----------------------------
-- Table structure for `blog_users`
-- ----------------------------
DROP TABLE IF EXISTS `blog_users`;
CREATE TABLE `blog_users` (
  `uid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '用户表主键id',
  `username` char(20) NOT NULL DEFAULT '' COMMENT '用户名',
  `nickname` varchar(25) NOT NULL DEFAULT '' COMMENT '昵称',
  `password` char(32) NOT NULL DEFAULT '' COMMENT '密码',
  `sex` enum('保密','女','男') NOT NULL DEFAULT '保密' COMMENT '性别',
  `phone` varchar(11) NOT NULL DEFAULT '' COMMENT '手机号',
  `email` varchar(100) NOT NULL DEFAULT '' COMMENT '邮箱',
  `is_admin` enum('否','是') NOT NULL DEFAULT '否' COMMENT '是否管理员',
  `is_lock` enum('正常','锁定') NOT NULL DEFAULT '正常' COMMENT '是否锁定',
  `session_validate` char(32) NOT NULL DEFAULT '' COMMENT '验证唯一登录',
  `login_date` char(10) NOT NULL DEFAULT '' COMMENT '登录时间',
  `reg_date` char(10) NOT NULL DEFAULT '' COMMENT '注册时间',
  PRIMARY KEY (`uid`),
  UNIQUE KEY `username_UNIQUE` (`username`)
) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=utf8 COMMENT='用户表';

